import React, {useState} from "react";
import "./AboutSection.css";
import {
    SiJavascript, SiJava, SiR, SiReact, SiHtml5,
    SiCss3, SiCplusplus, SiCsharp, SiNodeDotJs,
    SiAdobephotoshop, SiAdobeindesign,
    SiAdobeillustrator, SiPostgresql, SiPython, SiC, SiMysql
} from 'react-icons/si';
import {DiGoogleCloudPlatform} from 'react-icons/di';
import {Button} from "./Button";
import {FaTimes} from "react-icons/fa";

function AboutSection() {
    const [openModal, setOpenModal] = useState(false);
    const Modal = ({id,closeModal,header,text}) => (
        <div className={id}>
            <div className='bg' onClick={() => closeModal(false)}>
                <div className='modalContainer'>
                    <FaTimes className = 'close' onClick={() => closeModal(false)} />
                    <div className='body'>
                        <h1>{header}</h1>
                        <p>{text}</p>
                    </div>
                </div>
            </div>
        </div>
    );
    const open = () => (
      e => {
          if (e.target.getAttribute("id")) {
              setOpenModal(true);
              console.log(e);
          }
          else {
              setOpenModal(false);
          }
      }
    );

    console.log(open());

    const program = (<>
        <div>
            <p><SiJava/>Java</p>
            <p><SiR/>R</p>
            <p><SiPython/>Python</p>
            <p><SiC/>C</p>
            <p><SiCplusplus/>C++</p>
            <p><SiCsharp/>C#</p>
        </div>
    </>)

    const web = (
        <>
            <div>
                <p><SiHtml5/>HTML 5</p>
                <p><SiCss3/>CSS 3</p>
                <p><SiJavascript/>JavaScript</p>
                <p><SiReact/>ReactJS</p>
                <p><SiNodeDotJs/>NodeJS</p>
            </div>
        </>
    )

    const graphic = (
        <>
            <div>
                <p><SiAdobephotoshop/>Adobe Photoshop</p>
                <p><SiAdobeillustrator/>Adobe Illustrator</p>
                <p><SiAdobeindesign/>Adobe InDesign</p>
            </div>
        </>
    )

    const database = (
        <>
            <div>
                <p><DiGoogleCloudPlatform/>GCP</p>
                <p><SiPostgresql/>postgreSQL</p>
                <p><SiMysql/>MySQL</p>
            </div>
        </>
    )

    return (
        <>
            <div className='intro'>
                    <h1>About Me</h1>
                    <h3>Computer Science, Mathematics, Artist, Gamer</h3>
                    <p>I am a student majoring in Computer Science and Mathematics. I am a practical person and enjoy learning through doing things. I love to code and design applications,
                        either it is frontend or backend. My favorite math topics are multivariables calculus, linear optimization and statistics. I am also a self-taught artist, who hobby is digital art.
                        Aside from showcasing my creativity through programming, I also love art as well as gaming. Some of my favorite
                        games are MOBA likes League of Legend: Wildrift, Role Playing like Personas and Shin Megami Tensei, and Simulation
                    like Stardew Valley, Rune Factory, and Story of Seasons.</p>
            </div>
            <div className='modal-btn'>
                <Button onClick={() => open("1")} className = 'btn' buttonStyle='btn--primary' buttonSize='btn--large'>
                    Open
                </Button>
            {openModal ? <Modal id ="1" closeModal={setOpenModal}
            header="Programming Languages" text={program}/> : null}
            </div>

            <div className='modal-btn'>
                <Button onClick={() => open("2")} className = 'btn' buttonStyle='btn--primary' buttonSize='btn--large'>
                    Open
                </Button>
            {openModal ? <Modal id="2" closeModal={setOpenModal} header="Web Development" text={web}/> : null}
            </div>

            <div className='modal-btn'>
                <Button onClick={() => open("3")} className = 'btn' buttonStyle='btn--primary' buttonSize='btn--large'>
                    Open
                </Button>
            {openModal ? <Modal id="3" closeModal={setOpenModal} header="Graphic Design" text={graphic}/> : null}
            </div>

            <div className='modal-btn'>
                <Button onClick={() => open("4")} className = 'btn' buttonStyle='btn--primary' buttonSize='btn--large'>
                    Open
                </Button>
            {openModal ? <Modal id = "4" closeModal={setOpenModal} header="Cloud & Database" text={database}/> : null}
            </div>
        </>

    )
}

export default AboutSection;
