import React, {useState} from "react";
import {Link} from "react-router-dom";
import {CgWebsite} from "react-icons/cg";
import {FaBars, FaTimes} from "react-icons/all";
import {Button} from "./Button";

function Navbar() {
    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);

    const [button, setButton] = useState(true);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false)
        }
        else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);

    return(
        <>
            <div className="navbar">
                <div className="navbar-container container">
                    <Link className="navbar-logo">
                        <CgWebsite className="navbar-icon"/>
                        PORTFOLIO
                    </Link>
                    <div className="menu-icon" onClick={handleClick}>
                        {click ? <FaTimes /> : <FaBars/>}
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                        <li className='nav-item'>
                            <Link to='/' className='nav-links'>
                                About Me
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/projects' className='nav-links'>
                                Projects
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/resume' className='nav-links'>
                                Resume
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/contact' className='nav-links'>
                                Contact
                            </Link>
                        </li>
                        <li className="nav-btn">
                            {button ? (
                                <Link to='/email' className="btn-link">
                                    <Button buttonStyle='btn--outline'>Email</Button>
                                </Link>
                            ): (
                                <Link className="btn-link">
                                    <Button to='email' buttonStyle="btn--outline" buttonSize='btn--mobile'>Email</Button>
                                </Link>
                            )}
                        </li>
                    </ul>
                </div>
            </div>
        </>
    )
}

export default Navbar;
