import React from "react";
import {Button} from "./Button";
import "./Hero.css";
import video from './nebula.mp4'

function Hero() {
    return (
        <div className='hero-container'>
            <video autoPlay={true} loop={true} muted={true} src={video}/>
            <h1>Dusty Dang</h1>
            <p>Computer Science, Mathematics, Artist, Gamer</p>
            <p className='intro'>I am a student majoring in Computer Science and Mathematics. I love to code and design applications,
                either it is frontend or backend. I am also a self-taught artist, who hobby is digital art.
            Aside from showcasing my creativity through programming, I also love art as well as gaming. Some of my favorite
            games are MOBA likes League of Legend: Wildrift, Role Playing like Personas and Shin Megami Tensei, and Simulation
            like Stardew Valley, Ruin Factory, and Story of Seasons</p>
            <div className="hero-btn">
                <Button className = 'btn' buttonStyle='btn--outline' buttonSize='btn--large'>
                    Projects
                </Button>
            </div>
            <p className= 'credit'>Video and Images Credits:</p>
            <a href="https://www.vecteezy.com/free-photos">Free Stock photos by Vecteezy</a>
        </div>
    )
}

export default Hero;
